mapper.select:
select DISTINCT sm.perms from sys_menu sm join sys_role_menu srm on sm.menu_id = srm.menu_id join sys_role sr on srm.role_id = sr.role_id join sys_user_role sur ON srm.role_id = sur.role_id where user_id = #{userId} ANd sr.status = 0
AND sur.address_info_id = #{addressInfoId}
and sm.perms is not null

select sua.user_type,sua.associate_time,su.user_id,su.username,su.name,su.last_login_time,su.mobile from
sys_user_address sua
join sys_user su on sua.user_id = su.user_id

    sua.address_info_id = #{form.addressInfoId}

        and su.name like concat('%',#{form.name},'%')


        and su.mobile = #{form.mobile}


        and sua.user_type = #{form.userType}


order by user_type desc
